import React from 'react';

function Sidebar({ setSection }) {
  return (
    <aside className="w-64 bg-gray-800 flex flex-col items-center p-6">
      <img src="/profile.jpg" alt="Profile" className="rounded-full w-28 mb-4" />
      <h1 className="text-2xl font-bold text-center">Lalith Kumar<br /><span className="text-blue-400">Goud</span></h1>
      <p className="text-sm text-gray-400 mb-6">Senior Frontend Developer</p>
      <nav className="flex flex-col gap-4 w-full text-center">
        <button onClick={() => setSection('about')} className="hover:text-blue-400">About</button>

        <button onClick={() => setSection('experience')} className="hover:text-blue-400">Work Experience</button>
        <button onClick={() => setSection('contact')} className="hover:text-blue-400">Contact</button>
      </nav>
    </aside>
  );
}

export default Sidebar;
